; ==============================================================================
; INTERACID_PUSHBLOCK: a block in the process of being pushed.
; ==============================================================================
interactionCode14:
	ld e,Interaction.state		; $4430
	ld a,(de)		; $4432
	rst_jumpTable			; $4433
	.dw @state0
	.dw @state1

; State 0: block just pushed.
