	ld h,d			; $4463
	ldbc SPEED_80, $20		; $4464
	ld a,(wBraceletLevel)		; $4467
	cp $02			; $446a
	jr nz,+			; $446c
	ld l,Interaction.var34		; $446e
	bit 5,(hl)		; $4470
	jr nz,+			; $4472
	ldbc SPEED_c0, $15		; $4474
+
	ld l,Interaction.speed		; $4477
	ld (hl),b		; $4479
	ld l,Interaction.counter1		; $447a
	ld (hl),c		; $447c

	ld l,Interaction.angle		; $447d
	ld a,(hl)		; $447f
	or $80			; $4480
	ld ($cca6),a		; $4482

