	ld a,(de)		; $44ab
	or a			; $44ac
	jr z,++			; $44ad
	ld b,a			; $44af
	ld e,Interaction.var30		; $44b0
	ld a,(de)		; $44b2
	ld c,a			; $44b3
	ld a,b			; $44b4
	call setTile		; $44b5
++
