	ld e,Interaction.var34		; $44b8
	ld a,(de)		; $44ba
	rlca			; $44bb
	jr nc,++		; $44bc
	xor a			; $44be
	ld (wDisabledObjects),a		; $44bf
	ld a,SND_SOLVEPUZZLE		; $44c2
	call playSound		; $44c4
++
