	ld a,(de)		; $4515
	ld b,a			; $4516
--
	ldi a,(hl)		; $4517
	or a			; $4518
	ret z			; $4519
	cp b			; $451a
	jr z,@match		; $451b
	inc hl			; $451d
	inc hl			; $451e
	inc hl			; $451f
	jr --			; $4520

@match:
	; Write data to var31-var34.
	ld (de),a		; $4522
	ldi a,(hl)		; $4523
	inc e			; $4524
	ld (de),a		; $4525
	ldi a,(hl)		; $4526
	inc e			; $4527
	ld (de),a		; $4528
	ldi a,(hl)		; $4529
