	ld a,(wSwitchState)		; $4aab
	ld b,a			; $4aae
	ld e,Interaction.var03		; $4aaf
	ld a,(de)		; $4ab1
	and b			; $4ab2
	ld c,$00		; $4ab3
	jr nz,+			; $4ab5
	ld c,$01		; $4ab7
+
	dec e			; $4ab9
	ld a,(de) ; a = [subid] (subid is 0 if facing left, 2 if facing right)
	or c			; $4abb
	ld e,Interaction.var30		; $4abc
	ld (de),a		; $4abe

	call interactionSetAnimation		; $4abf

	call objectGetTileAtPosition		; $4ac2
	dec h ; h points to wRoomCollisions
	dec l			; $4ac6

	; a = [var30]*3
	ld e,Interaction.var30		; $4ac7
	ld a,(de)		; $4ac9
	ld b,a			; $4aca
	add a			; $4acb
	add b			; $4acc

	ld bc,@collisions		; $4acd
	call addAToBc		; $4ad0
	ld a,(bc)		; $4ad3
	ldi (hl),a		; $4ad4
	inc bc			; $4ad5
	ld a,(bc)		; $4ad6
	ld (hl),a		; $4ad7

	inc bc			; $4ad8
	ld a,(bc)		; $4ad9
	add l			; $4ada
	ld l,a			; $4adb
	inc h			; $4adc
	ld a,(de)		; $4add
	rrca			; $4ade
	jr c,+			; $4adf
	ld (hl),$5e		; $4ae1
	ret			; $4ae3
+
	ld (hl),$00		; $4ae4
	ret			; $4ae6

@collisions:
	.db $00 $0a  $ff ; Gate facing right
	.db $0c $0a  $ff
	.db $05 $00  $00 ; Gate facing left
	.db $05 $0c  $00


