; Reads the static object buffer and creates the objects for the room.
; @addr{5015}
parseStaticObjects:
	ld de,wStaticObjects		; $5015
@next:
	ld c,e			; $5018
	ld a,(de)		; $5019
	or a			; $501a
	jr z,@noMatch		; $501b

	inc e			; $501d
	ld a,(de)		; $501e
	ld b,a			; $501f
	ld a,(wActiveRoom)		; $5020
	cp b			; $5023
	jr z,@foundRoom		; $5024
@noMatch:
	ld a,c			; $5026
	add $08			; $5027
	ld e,a			; $5029
	cp <(wStaticObjects+wStaticObjects.size)	; $502a
	jr c,@next		; $502c
	ret			; $502e

@foundRoom:
	dec e			; $502f
	ld a,(de)		; $5030
	bit 7,a			; $5031
	jr nz,@noMatch		; $5033

	and $7f			; $5035
	rst_jumpTable			; $5037
.dw @end
.dw @end
.dw @end
.dw @interaction
.dw @enemy
.dw @part

@end:
	ld a,e			; $5044
	add $08			; $5045
	ld e,a			; $5047
	jr @next		; $5048

@interaction:
	call getFreeInteractionSlot		; $504a
	jr nz,@end		; $504d
	jr ++			; $504f

@enemy:
	call getFreeEnemySlot		; $5051
	jr nz,@end		; $5054
	jr ++			; $5056

@part:
	call getFreePartSlot		; $5058
	jr nz,@end		; $505b
++
	inc e			; $505d
	inc e			; $505e
	ld a,(de)		; $505f
	bit 7,a			; $5060
	jr z,+			; $5062

	dec l			; $5064
	set 1,(hl)		; $5065
	inc l			; $5067
+
	; B2: ID
	and $7f			; $5068
	ldi (hl),a		; $506a

	; B3: Sub ID
	inc e			; $506b
	ld a,(de)		; $506c
	ld (hl),a		; $506d

	; B4: Y
	ld a,l			; $506e
	add Object.yh-Object.subid	; $506f
	ld l,a			; $5071
	inc e			; $5072
	ld a,(de)		; $5073
	ldi (hl),a		; $5074

	; B5: X
	inc l			; $5075
	inc e			; $5076
	ld a,(de)		; $5077
	ld (hl),a		; $5078

	; Set RelatedObj1 (actually a pointer to its position in wStaticObjects)
	ld a,l			; $5079
	add Object.relatedObj1-Object.xh		; $507a
	ld l,a			; $507c
	ld a,e			; $507d
	and $f8			; $507e
	ld e,a			; $5080
	ldi (hl),a		; $5081
	ld (hl),d		; $5082
	jr @end		; $5083

