loadStaticObjects_body:
	call clearStaticObjects		; $5085
	ld a,(wDungeonIndex)		; $5088
	ld hl,staticDungeonObjects	; $508b
	rst_addDoubleIndex			; $508e
	ldi a,(hl)		; $508f
	ld h,(hl)		; $5090
	ld l,a			; $5091
	ld de,wStaticObjects		; $5092
