	ldi a,(hl)		; $5095
	cp $ff			; $5096
	ret z			; $5098
	ld (de),a		; $5099
	ld b,$05		; $509a
@copyLoop:
	ldi a,(hl)		; $509c
	inc e			; $509d
	ld (de),a		; $509e
	dec b			; $509f
	jr nz,@copyLoop		; $50a0

	inc e			; $50a2
	inc e			; $50a3
	inc e			; $50a4
	jr @next		; $50a5


