	; Check enemies
	ld d,FIRST_ENEMY_INDEX		; $4157
@nextEnemy:
	call _enemyGetObjectGfxIndex		; $4159
	call _markLoadedObjectGfxUsed		; $415c
	inc d			; $415f
	ld a,d			; $4160
