	jr nz,--		; $46a5

	push bc			; $46a7
	ld a,e			; $46a8
	call hexToDec		; $46a9
	swap c			; $46ac
	or c			; $46ae
	ld (wNumUnappraisedRingsBcd),a		; $46af
	pop bc			; $46b2
	ld b,e			; $46b3
	pop de			; $46b4
	ret			; $46b5

