	; Write to w7LineTextBuffer
	ld (de),a		; $50a6
	push de			; $50a7
	push hl			; $50a8

	; Write to w7LineAttributesBuffer
	ld hl,w7TextAttribute		; $50a9
	ld a,e			; $50ac
	add $10			; $50ad
	ld e,a			; $50af
	ldd a,(hl)		; $50b0
	ld (de),a		; $50b1

	; Write w7CharacterDisplayLength to w7LineDelaysBuffer
	ld a,e			; $50b2
	add $10			; $50b3
	ld e,a			; $50b5
	dec l			; $50b6
	ldd a,(hl)		; $50b7
	ld (de),a		; $50b8

	; Write w7TextSound to w7LineSoundsBuffer
	ld a,e			; $50b9
	add $10			; $50ba
	ld e,a			; $50bc
	ldd a,(hl)		; $50bd
	ld (de),a		; $50be

