	ld a,e			; $50bf
	add $10			; $50c0
	ld e,a			; $50c2
	ld a,(hl)		; $50c3
	ld (de),a		; $50c4
	ld (hl),$00		; $50c5

	pop hl			; $50c7
	pop de			; $50c8
	ld a,(de)		; $50c9
	inc e			; $50ca
	ret			; $50cb

