;;
; Save the current address of text being read.
; @param hl Current address of text
; @addr{567f}
_pushToTextStack:
	push de			; $567f
	push bc			; $5680
	push hl			; $5681
	ld hl,w7TextStack+$1b		; $5682
	ld de,w7TextStack+$1f		; $5685
	ld b,$1c		; $5688
-
	ldd a,(hl)		; $568a
	ld (de),a		; $568b
	dec e			; $568c
	dec b			; $568d
	jr nz,-		; $568e

	; hl = w7TextStack
	inc l			; $5690

	ld de,w7ActiveBank		; $5691
	ld a,(de)		; $5694
	ldi (hl),a		; $5695
	pop de			; $5696
	ld (hl),e		; $5697
	inc l			; $5698
	ld (hl),d		; $5699
	inc l			; $569a
	ld a,(wTextIndexH)		; $569b
	ld (hl),a		; $569e
	ld h,d			; $569f
	ld l,e			; $56a0
	pop bc			; $56a1
	pop de			; $56a2
	ret			; $56a3

