	ldd a,(hl)		; $56a9
	ld (wTextIndexH),a		; $56aa
	ldd a,(hl)		; $56ad
	ld de,w7TextAddressH		; $56ae
	ld (de),a		; $56b1
	ld b,a			; $56b2
	ldd a,(hl)		; $56b3
	dec e			; $56b4
	ld (de),a		; $56b5
	ld c,a			; $56b6
	ld a,(hl)		; $56b7
	dec e			; $56b8
	ld (de),a		; $56b9
	push bc			; $56ba
	ld de,w7TextStack+4		; $56bb
	ld b,$1c		; $56be
-
	ld a,(de)		; $56c0
	ldi (hl),a		; $56c1
	inc e			; $56c2
	dec b			; $56c3
	jr nz,-			; $56c4

	xor a			; $56c6
	ldi (hl),a		; $56c7
	ldi (hl),a		; $56c8
	ldi (hl),a		; $56c9
	ld (hl),a		; $56ca
	pop hl			; $56cb
	pop bc			; $56cc
	pop de			; $56cd
	ret			; $56ce

